INSERT INTO `Interest` VALUES ('1', '九寨沟风景名胜区', '九寨沟自然保护区地势南高北低，山谷深切，高差悬殊。北缘九寨沟口海拔仅2000米，中部峰岭均在4000米以上，南缘达4500米以上，主沟长30多公里。世界自然遗产、国家重点风景名胜区、国家AAAAA级旅游景区、国家级自然保护区、国家地质公园、世界生物圈保护区网络，也是中国第一个以保护自然风景为主要目的的自然保护区。', '5A', '阿坝州', '九寨沟县', '阿坝州九寨沟县漳扎镇', 'http://sicnu3s.club:8080/images/interests/ID1.jpg', '513225');
INSERT INTO `Interest` VALUES ('2', '黄龙国家级风景名胜区', '黄龙以彩池、雪山、峡谷、森林“四绝”著称于世，再加上滩流、古寺、民俗称为“七绝”。景区由黄龙沟、丹云峡、牟尼沟、雪宝鼎、雪山梁、红星岩，西沟等景区组成。主要景观集中于长约3.6公里的黄龙沟，沟内遍布碳酸钙华沉积。并呈梯田状排列，以丰富的动植物资源享誉人间.', '5A', '阿坝州', '松潘县', '阿坝州松潘县瑟尔磋寨', 'http://sicnu3s.club:8080/images/interests/ID2.jpg', '513224');
INSERT INTO `Interest` VALUES ('3', '巴中市光雾山景区', '光雾山旅游景区位于华夏版图腹心、米仓山腹地，地处中国南北气候分界线，为南方的北方、北方的南方。南北冷暖气流在这里交汇，雨量充沛，森林植被原始丰茂，植被覆盖率达到98%，区内四季分明，空气清新，每立方厘米负氧离子含量达3万多个，造就了宜人居住的最佳自然环境。', '5A', '巴中市', '南江县', '巴中市南江县光雾山镇', 'http://sicnu3s.club:8080/images/interests/ID3.jpg', '511922');
INSERT INTO `Interest` VALUES ('4', '剑门蜀道剑门关旅游景区', '剑门蜀道历史悠久，肇始于西周，使用至民国，民间至今仍在使用，它是先秦古蜀道——金牛道的核心价值地段，历史跨越3000余年。至今仍遗存有众多的古桥梁、古建筑、古碑刻、古寺庙、古城址、行道树等大量珍贵文物', '5A', '广元市', '剑阁县', '广元市剑阁县下寺镇', 'http://sicnu3s.club:8080/images/interests/ID4.jpg', '510823');
INSERT INTO `Interest` VALUES ('5', '北川羌城旅游区', '北川羌城旅游区位于四川盆地西北部北川羌族自治县，由北川地震遗址区、北川新县城，北川地震纪念馆、北川羌族民俗博物馆、吉娜羌寨、维斯特游客中心等景点组成。', '5A', '绵阳市', '北川县', '绵阳市北川县永昌镇青片路', 'http://sicnu3s.club:8080/images/interests/ID5.jpg', null);
INSERT INTO `Interest` VALUES ('6', '阆中古城', '阆中古城位于四川省东北部、嘉陵江中上游，是中国四大古城之一，被全国名城保护专家誉为“中国保存最完好的古城”，拥有全国历史文化名城、中国优秀旅游城市、国家5A级旅游景区、中国春节文化之乡等多项殊荣', '5A', '南充市', '阆中市', '南充市阆中市阆水中路33号', 'http://sicnu3s.club:8080/images/interests/ID6.jpg', '511381');
INSERT INTO `Interest` VALUES ('7', '朱德故里景区', '朱德故里景区位于四川省南充市仪陇县马鞍镇。总体规划面积52.5平方公里，核心景区5.6平方公里。内辖5个村（社区），人口5385人，有耕地2641亩，植被覆盖率92.6%，是一个与村民共生共融的开放式、原生态景区。', '5A', '南充市', '仪陇县', '南充市仪陇县马鞍镇朱德故里景区管理局', 'http://sicnu3s.club:8080/images/interests/ID7.jpg', '511324');
INSERT INTO `Interest` VALUES ('8', '汶川特别旅游区', '汶川素有“阳光谷地、熊猫家园、康养汶川”之美誉，旅游资源得天独厚，有国家AAAAA级景区汶川特别旅游区、国家AAAA级景区大禹文化旅游区等景点，冬无严寒、夏无酷暑，风光秀美、特色鲜明。是华夏始祖大禹的出生地，是藏、羌、回、汉等各族群众交汇融合的地带，是全国四大羌族聚居县之一，国家羌文化生态体验区', '5A', '阿坝州', '汶川县', '阿坝州汶川县国家级风景区管理局', 'http://sicnu3s.club:8080/images/interests/ID8.jpg', '513221');
INSERT INTO `Interest` VALUES ('9', '都江堰旅游景区', '都江堰景区位于四川省成都市都江堰市城西，坐落在成都平原西部的岷江上，始建于秦昭王末年（约公元前256～前251），是蜀郡太守李冰父子在前人鳖灵开凿的基础上组织修建的大型水利工程，由分水鱼嘴、飞沙堰、宝瓶口等部分组成，两千多年来一直发挥着防洪灌溉的作用，使成都平原成为水旱从人、沃野千里的\"天府之国\"', '5A', '成都市', '都江堰市', '成都都江堰市城西', 'http://sicnu3s.club:8080/images/interests/ID9.jpg', '510181');
INSERT INTO `Interest` VALUES ('10', '青城山景区', '青城山为中国道教发源地之一，属道教名山之一。位于四川省成都市都江堰市西南，古称“丈人山”，东距成都市68公里，处于都江堰水利工程西南10公里处。主峰老霄顶海拔1600米。在四川名山中与剑门之险、峨嵋之秀、夔门之雄齐名，有“青城天下幽”之美誉。', '5A', '成都市', '都江堰市', '成都市都江堰市青城镇', 'http://sicnu3s.club:8080/images/interests/ID10.jpg', '510181');
INSERT INTO `Interest` VALUES ('11', '邓小平故里旅游区', '该景区是集缅怀纪念、爱国主义教育、古镇文化、社会主义新农村展示、休闲度假于一体的复合型旅游景区。主要景点有邓小平故居、洗砚池、翰林院子、蚕房院子、北山小学堂、清水塘、神道碑、德政坊等近20处邓小平童年及青少年时期活动场所。', '5A', '广安市', '广安区', '广安市广安区协兴镇牌坊社区420号', 'http://sicnu3s.club:8080/images/interests/ID11.jpg', '511602');
INSERT INTO `Interest` VALUES ('12', '安仁古镇景区', '安仁古镇包含公馆老街、刘氏庄园博物馆、建川博物馆三大核心资源。拥有国家4A级旅游景区2个、全国最大保存完好中西合璧的老公馆群落27座、现代博物馆场馆48座、文保单位16处、藏品1000余万件、国家一级文物3655件。', '5A', '成都市', '大邑县', '成都市大邑县安仁镇金桂街15号', 'http://sicnu3s.club:8080/images/interests/ID12.jpg', '510129');
INSERT INTO `Interest` VALUES ('13', '碧峰峡旅游景区', '碧峰峡景区由野生动物世界、生态峡谷景区、大熊猫三个部分组成。植被、峡景和瀑布是碧峰峡景区的鲜明特色。峡内林木葱郁，苍翠欲滴，峰峦叠嶂，崖壑峥嵘。', '5A', '雅安市', '雨城区', '雅安市雨城区碧峰峡镇', 'http://sicnu3s.club:8080/images/interests/ID13.jpg', '511802');
INSERT INTO `Interest` VALUES ('14', '泸定海螺沟冰川森林公园', '海螺沟是青藏高原东缘的极高山地，以低海拔现代冰川著称于世。海螺沟有“五大特色”，即日照金山、冰川倾斜、雪谷温泉、原始森林和康巴藏族风情。“六绝”，即雪山、云雾、冰川、原始森林、温泉、红石。', '5A', '甘孜州', '泸定县', '甘孜州泸定县磨西镇', 'http://sicnu3s.club:8080/images/interests/ID14.jpg', '513322');
INSERT INTO `Interest` VALUES ('15', '峨眉山风景名胜区', '峨眉山自然遗产极其丰富，素有天然“植物王国”、“动物乐园”、“地质博物馆”之美誉。文化遗产极其深厚，是中国佛教圣地，被誉为“佛国天堂”，是普贤菩萨的道场。以其“雄、秀、神、奇、灵”的自然景观和深厚的佛教文化，被联合国教科文组织列入《世界文化与自然遗产名录》。', '5A', '乐山市', '峨眉山市', '乐山市峨眉山市黄湾乡', 'http://sicnu3s.club:8080/images/interests/ID15.jpg', '511181');
INSERT INTO `Interest` VALUES ('16', '乐山大佛景区', '乐山大佛是唐代摩岩造像中的艺术精品之一，是世界上最大的石刻弥勒佛坐像。位于四川省乐山市南岷江东岸凌云寺侧，濒大渡河、青衣江和岷江三江汇流处', '5A', '乐山市', '市中区', '乐山市市中区篦子街', 'http://sicnu3s.club:8080/images/interests/ID16.jpg', '370103');
INSERT INTO `Interest` VALUES ('17', '甘孜州稻城亚丁景区', '稻城亚丁风景区位于四川甘孜藏族自治州南部，地处著名的青藏高原东部，横断山脉中段，属国家级自然保护区，省级风景名胜区。主要由\"仙乃日、央迈勇、夏诺多吉\"三座神山和周围的河流、湖泊和高山草甸组成。', '5A', '甘孜州', '稻城县', '甘孜州稻城县香格里拉镇', 'http://sicnu3s.club:8080/images/interests/ID17.jpg', '513337');
INSERT INTO `Interest` VALUES ('18', '若尔盖县花湖生态旅游区', '花湖位于四川若尔盖和甘肃郎木寺之间的213国道旁，是热尔大坝草原上的一个天然海子，花湖经典的时间是在五六月份，湖畔五彩缤纷，好像云霞委地，而湖中则开满了水妖一样的绚丽花朵。', '4A', '阿坝州', '若尔盖县', '阿坝藏族羌族自治州若尔盖县213国道', 'http://sicnu3s.club:8080/images/interests/ID18.jpg', '513232');
INSERT INTO `Interest` VALUES ('19', '黄河九曲第一湾', '黄河发源于青海巴颜卡拉山自西向东，迂回曲折，在四川若尔盖县唐克乡与白河汇合，形成了壮美的九曲黄河第一大转弯，隔河与甘肃省相望。九曲黄河第一弯作为黄河大草原最具代表性的景点，它以一种大度的姿态、平和的心态表现了黄河精神“宁静致远”的阴柔之美。', '4A', '阿坝州', '若尔盖县', '阿坝州若尔盖县达扎寺镇麦溪路', 'http://sicnu3s.club:8080/images/interests/ID19.jpg', '513232');
INSERT INTO `Interest` VALUES ('20', '石渠县巴格嘛呢景区', '巴格嘛呢墙全长1.6公里，厚约2-3米，最高处约3米，墙体全部由嘛呢石片垒成，石片上刻着的是六字箴言和《甘珠尔》、《丹珠尔》的部分经文。墙头上飘扬着五色经幡，墙体两边的窗口里摆放着各种佛像，雕刻精美传神。', '4A', '甘孜州', '石渠县', '石渠县长沙贡马乡', 'http://sicnu3s.club:8080/images/interests/ID20.jpg', '513332');
